"use strict";dotclear.ready(()=>{const e=dotclear.getData("typo");for(const o of e.items){const e=document.querySelectorAll(`form[action^="${o.url}"]`);for(const t of e)if(""===o.id||t.id===o.id)for(const e of o.selectors){const o=t.querySelectorAll(e);for(const e of o)!e||e.readOnly||e.disabled||e.addEventListener("blur",()=>{const o=e.value;""!==o&&dotclear.jsonServicesGet("typoTransform",t=>{t.ret&&o!==t.buffer&&(e.value=t.buffer)},{buffer:o})})}}});
